import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { Link } from 'react-router-dom';
import Spinner from 'react-bootstrap/Spinner';

const Products = () => {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        getProducts();
    }, []);

    const getProducts = async () => {
        setLoading(true);
        const response = await fetch("https://api.escuelajs.co/api/v1/products?limit=28");
        const responseData = await response.json();
        setData(responseData);
        setLoading(false);
    };

    const Loading = () => {
        return (
            <div className="mt-4">
                <Spinner animation="grow" />
                <Spinner animation="grow" />
                <Spinner animation="grow" />
                <Spinner animation="grow" />
                <Spinner animation="grow" />
                <Spinner animation="grow" />
                <Spinner animation="grow" />
            </div>
        );
    };

    const renderProducts = (products) => {
        return products.map((product) => (
            <div className="col-3 mt-5" key={product.id}>
                <Card className="border border-dark">
                    <Card.Img variant="top" style={{ height: '300px' }} src={product.images[0]} />
                    <Card.Body>
                        <Card.Title>{product.title.substring(0, 12)}</Card.Title>
                        <Card.Text className='fw-bold'>$ {product.price}</Card.Text>
                        <Link to={`/products/${product.id}`}><Button variant="dark">Buy Now</Button></Link>
                    </Card.Body>
                </Card>
            </div>
        ));
    };

    return (
        <div className="container mt-5 pb-5">
            <div className="row">
                <div className="col-lg-12">
                    <h1 className='display-6'>All Products</h1>
                    <hr />
                    <div className="row justify-content-center">
                        {loading ? <Loading /> : renderProducts(data)}
                    </div>
                </div>
            </div>
            <div className="row mt-5">
                <div className="col-lg-12">
                    <h1 className='display-6'>Category-wise Products</h1>
                    <hr />
                    <div className="row justify-content-center">
                        {loading ? <Loading /> : renderCategoryWiseProducts(data)}
                    </div>
                </div>
            </div>
        </div>
    );
};

const renderCategoryWiseProducts = (data) => {
    // Group products by category
    const groupedProducts = data.reduce((groups, product) => {
        const category = product.category.name;
        if (!groups[category]) {
            groups[category] = [];
        }
        groups[category].push(product);
        return groups;
    }, {});

    // Render products for each category
    return Object.keys(groupedProducts).map((categoryName) => {
        const products = groupedProducts[categoryName];
        return (
            <div key={categoryName}>
                <h2>{categoryName}</h2>
                <div className="row">
                    {renderProducts(products)}
                </div>
            </div>
        );
    });
};

export default Products;
