import React, { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { Link } from 'react-router-dom';
import Spinner from 'react-bootstrap/Spinner';

const Products = () => {
    const [data, setData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [showAll, setShowAll] = useState(false);

    useEffect(() => {
        getProducts(32); // Fetch 32 products initially
    }, []);

    const getProducts = async (limit) => {
        setLoading(true);
        const response = await fetch(`https://api.escuelajs.co/api/v1/products?limit=${limit}`);
        const responseData = await response.json();
        setData(responseData);
        setFilteredData(responseData);
        setLoading(false);
    };

    const Loading = () => {
        return (
            <div className="mt-4 ">
                <Spinner animation="grow" />
                <Spinner animation="grow" />
                <Spinner animation="grow" />
                <Spinner animation="grow" />
                <Spinner animation="grow" />
                <Spinner animation="grow" />
                <Spinner animation="grow" />
            </div>
        );
    };

    const toggleShowAll = () => {
        setShowAll(!showAll);
        if (showAll) {
            setFilteredData(data.slice(0, 32)); // Limit to 32 products when toggling back from "View All"
        } else {
            setFilteredData(data); // Show all products when toggling to "View All"
        }
    };

    return (
        <div>
            <div className="container mt-5 pb-5">
                <div className="row">
                    <div className="col-lg-12">
                        <h1 className='display-6'>All Products</h1>
                        <hr />
                        <div className="buttons">
                            <Button onClick={() => toggleShowAll()} className='me-2' variant="outline-dark">
                                {showAll ? "Show Less" : "View All"}
                            </Button>
                        </div>
                        <div className="row">
                            {loading ? <Loading /> : (
                                filteredData.map((item) => {
                                    const imageUrl = item.images && item.images.length > 0 ? item.images[0] : '';
                                    return (
                                        <div className="col-3 mt-5" key={item.id}>
                                            <Card className="border border-dark">
                                                <Card.Img variant="top" style={{ height: '300px' }} src={imageUrl} />
                                                <Card.Body>
                                                    <Card.Title>{item.title.substring(0, 12)}</Card.Title>
                                                    <Card.Text className='fw-bold'>
                                                        $ {item.price}
                                                    </Card.Text>
                                                    <Link to={`/products/${item.id}`}> <Button variant="dark">Buy Now</Button></Link>
                                                </Card.Body>
                                            </Card>
                                        </div>
                                    )
                                })
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Products;
